import React from "react";
import cardsData from "../cards.json";
import Card from "./Card";
import Select from "react-select";

const selectOptions = [
	{ value: "+", label: "Ascending" },
	{ value: "-", label: "descending" },
	{ value: "vanilla", label: "Alfabethic" },
];
class Cards extends React.Component {
	constructor(props) {
		super(props);
		this.state = { query: "", sort: "" };
		this.handleChange = this.handleChange.bind(this);
	}
	handleChange(event) {
		const target = event.target;
		const value = target.value;
		const name = target.name;
		this.setState({ [name]: value });
	}
	render() {
		let cardsTemp = cardsData;
		const cards = cardsTemp
			.filter((card) =>
				card.date.toLowerCase().includes(this.state.query.toLowerCase())
			)
			.sort((a, b) => {
				a.date > b.date ? 1 : -1;
			})
			.map((item) => {
				return <Card key={item.id} item={item} />;
			});
		return (
			<div>
				<form>
					Sort:&nbsp;
					<Select name="sort" options={selectOptions} />
					<br />
					<label style={{ paddingTop: 6 }}>Date query&nbsp;</label>
					<input
						name="query"
						value={this.state.query}
						onChange={this.handleChange}
						placeholder="query"
						type="text"
					/>
				</form>
				<div id="cards" className="row">
					{cards}
				</div>
			</div>
		);
	}
}

export default Cards;
