			<div>
				<form>
					<label>What are you searching for </label>
					<input
						name="query"
						value={this.state.query}
						onChange={this.handleChange}
						placeholder="query"
						type="text"
					/>
				</form>
				<div id="cards" className="row">
					{cards}
				</div>
			</div>
		);
	}
}

export default Cards;
