import React from "react";
import Card from "./components/Card";

class List extends React.Component{
    constructor(){
        super();
        this.state={
            cards:[]
        }
        this.fetchItems=this.fetchItems.bind(this);
    }
    async componentDidMount(){
        this.fetchItems();
    }
    async fetchItems(){
        const data= await fetch('https://dog.ceo/api/breed/husky/images/random/6');
        const items= await data.json();
        let i=0;
        const cards=items.message.map(item=>{
            console.log(item);
            return <Card key={i++} imgUrl={item}/>
        });
        this.setState({cards:this.state.cards.concat(cards)});
        
    }


    render(){
        return(
            <div id="cards" className="row">
                {this.state.cards}
                <button className="btn btn-primary" onClick={this.fetchItems}>More</button>
            </div>);
        }
}
export default List;
